#!/bin/bash

usage ()
{
    echo "Usage:  audio-client <port>"
}


if [ $# -ne 1 ]; then
    usage
    exit 0
fi


## 44.1 kHz mono, or else update the config string
gst-launch-1.0 -v udpsrc port=$1 caps="application/x-rtp, media=(string)audio, clock-rate=(int)44100, encoding-name=(string)VORBIS, encoding-params=(string)1, configuration=(string)\"\\=\\=\", payload=(int)96" ! rtpvorbisdepay ! vorbisdec ! audioconvert ! autoaudiosink sync=false

